/*eslint-disable*/
import React, { useEffect, useState } from 'react'
import Header from '../components/Header';
import fetch from 'isomorphic-unfetch';
import Task from '../components/Tasks';

export default function Home(props) {

  const [id, setId] = useState('');

  const [taskArr, getTasks] = useState([]);

  useEffect(() => {
    const cookie = window.document.cookie.split(';')[0].slice(3);
    console.log('Use Effect Cookie:', cookie);
    setId(cookie);
  });


  fetch(`/home/${id}`)
    .then((data) => data.json())
    .then((res) => {
      const arr = res.map((el) => {
        return (<Task
          id={el.id}
          task={el.tasks}
        />)
      })
      getTasks(arr);
      return;
    })


  return (
    <div>
      <Header />
      {taskArr}
      <h3>{props.quote}</h3> 
      <h5>-{props.author}</h5>
    </div>
  );
}

// const cookie = window.document.cookie.split(';')[0];
// cookie.slice(0, 2);
// console.log(`COOKIE : ${cookie}.`);

Home.getInitialProps = async function() {
  const res = await fetch('https://api.quotable.io/random');
  const data = await res.json();
  console.log(`Quote data fetched. content is: ${data.content}`);

  return { 
    quote: data.content,
    author: data.author,
  };
}
