import fetch from 'isomorphic-unfetch';

const Quotes = props => (
    <h3>
        {props.quote}
    </h3>
)

Quotes.getInitialProps = async function() {
    const res = await fetch('https://api.quotable.io/random');
    const quote = await res.json();
    console.log(quote);

    return { quote: quote.content };
}

export default Quotes;
