/*eslint-disable*/
import React, { useEffect, useState } from 'react'
import Header from '../components/Header';
import fetch from 'isomorphic-unfetch';
import Task from '../components/Tasks';

export default function Home(props) {

  const [id, setId] = useState('');

  const [taskArr, getTasks] = useState([]);

  useEffect(() => {
    const cookie = window.document.cookie.split(';')[0].slice(3);
    console.log('Use Effect Cookie:', cookie);
    setId(cookie);
  });

  // useEffect(() => {
  //   fetch(`/home/${id}`)
  //     .then((data) => data.json())
  //     .then((res) => {
  //       getTasks(res);
  //     })
  //     .catch((err) => console.log(err))
  // }, [id]);

  const fetchTasks = async () => {
    const apiCall = await fetch(`/home/${id}`);
    const data = await apiCall.json();
    getTasks(data);
  }

  useEffect(() => {
    fetchTasks();
  }, [taskArr]);

  const allTasks = taskArr.map((el) => {
    return (<Task
      id={el.id}
      task={el.tasks}
    />)
  })

  return (
    <div>
      <Header />
      {allTasks}
      <h3>{props.quote}</h3> 
      <h5>-{props.author}</h5>
    </div>
  );
}

Home.getInitialProps = async function() {
  const res = await fetch('https://api.quotable.io/random');
  const data = await res.json();
  console.log(`Quote data fetched. content is: ${data.content}`);

  return { 
    quote: data.content,
    author: data.author,
  };
}

